mapper.select:
SELECT PRODUCT_ID AS productId, CATEGORY_ID AS categoryId, PRODUCT_NAME AS name, BRAND_NAME AS brandName, PRICE AS price, STOCK_QUANTITY AS stockQuantity, DESCRIPTION AS description, STATUS AS status, IMAGE_URL AS imageUrl FROM PRODUCT
AND CATEGORY_ID = #{categoryId}
ORDER BY PRODUCT_ID DESC

mapper.update:
UPDATE PRODUCT
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE PRODUCT_ID = #{productId}
